mapper.select:
select b.*,i.problems_and_needs_faced from server_take_case_info i left join pi_children_base_info b on i.child_id =b.child_id join server_child_status_info s on s.sta_id=i.sta_id


        (i.child_name like concat('%', #{childName}, '%') or b.child_id_no like concat('%', #{childName}, '%'))


        and i.child_id = #{childId}


        and b.area like concat('%', #{area}, '%')


        and s.case_status = #{caseStatus}


        and i.child_id not in (select child_id from server_estimate_info group by child_id)


        and i.creator = #{creator}


order by i.case_date desc

select b.* from server_estimate_info i left join pi_children_base_info b on i.child_id =b.child_id join server_child_status_info s on s.sta_id=i.sta_id


        (i.child_name like concat('%', #{childName}, '%') or b.child_id_no like concat('%', #{childName}, '%'))


        and i.child_id = #{childId}


        and b.area like concat('%', #{area}, '%')


        and s.case_status = #{caseStatus}


        and s.estimate_status = #{estimateStatus}


        and i.child_id not in (select child_id from server_plan_info group by child_id)


        and i.creator = #{creator}


order by i.estimate_date desc

select b.* from server_plan_info i left join pi_children_base_info b on i.child_id =b.child_id join server_child_status_info s on s.sta_id=i.sta_id


        (i.child_name like concat('%', #{childName}, '%') or b.child_id_no like concat('%', #{childName}, '%'))


        and i.child_id = #{childId}


        and b.area like concat('%', #{area}, '%')


        and s.plan_status = #{planStatus}


        and i.child_id not in (select child_id from server_interview_intervention_info group by child_id)
        and i.child_id not in (select child_id from server_available_resources_info group by child_id)


        and i.creator = #{creator}


order by i.plan_date desc

select b.*,c.problems_and_needs_faced from server_interview_intervention_info i left join pi_children_base_info b on i.child_id =b.child_id join server_child_status_info s on s.sta_id=i.sta_id
left join server_take_case_info c on c.child_id =b.child_id


        (i.child_name like concat('%', #{childName}, '%') or b.child_id_no like concat('%', #{childName}, '%'))


        and i.child_id = #{childId}


        and b.area like concat('%', #{area}, '%')


        and s.intervention_status = #{interventionStatus}


        and i.child_id not in (select child_id from server_evaluate_info group by child_id)


        and i.creator = #{creator}


union
select b.*,c.problems_and_needs_faced from server_available_resources_info i left join pi_children_base_info b on i.child_id =b.child_id join server_child_status_info s on s.sta_id=i.sta_id
left join server_take_case_info c on c.child_id =b.child_id


        (i.child_name like concat('%', #{childName}, '%') or b.child_id_no like concat('%', #{childName}, '%'))


        and i.child_id = #{childId}


        and b.area like concat('%', #{area}, '%')


        and s.intervention_status = #{interventionStatus}


        and i.child_id not in (select child_id from server_evaluate_info group by child_id)


        and i.creator = #{creator}

select b.*,c.problems_and_needs_faced,c.case_date,ss.expected_goal, case when ss.expected_execution is null and ss.expected_execution = '' then ss.implementation_status else concat(ss.expected_execution , ';' , ss.implementation_status) end as expected_execution from server_evaluate_info i left join pi_children_base_info b on i.child_id =b.child_id join server_child_status_info s on s.sta_id=i.sta_id left join server_take_case_info c on c.child_id =b.child_id left join (select i.* from server_plan_info i join (select spi.child_id,max(spi.create_time) as create_time from server_plan_info spi group by spi.child_id) as sp on i.child_id =sp.child_id and i.create_time =sp.create_time) as ss on ss.child_id =b.child_id
(i.child_name like concat('%', #{childName}, '%') or b.child_id_no like concat('%', #{childName}, '%'))
and i.child_id = #{childId}
and b.area like concat('%', #{area}, '%')
and s.evaluate_status = #{evaluateStatus}
and i.child_id not in (select child_id from server_case_closed_info group by child_id)
and i.creator = #{creator}
order by i.evaluate_date desc
