mapper.select:
SELECT m.manage_id,apply_no,loan_type,loan_type_id,is_loop,company_name,company_id,project_name,project_id,is_syndicated_loan, loan_organization,loan_organization_id,bank_account,bank_account_id,business_unit,business_unit_id, contract_name,contract_num,loan_term_month,loan_term_year,contract_sign_date,contract_end_date,last_withdrawal_date, currency_type,exchange_rate,contract_amount,amount_after_discount,interest_rate_type,interest_rate_type_id,interest_rate_float_way, float_rate,contract_rate,change_status,adjust_interest_way,adjust_interest_date,adjust_interest_specific_time, adjust_interest_interval,interest_days,float_interest_way_desc,grace_time,commitment_fee_amount,commitment_fee_paid, commitment_fee_rate,guarantee_type,guarantee_person,loan_purpose,service_charge_amount,service_charge_rate, comprehensive_cost,qinghu_status,remarks,create_time,creator,update_time,updater,version,loan_id,deleted, approval_time,approval_status,status,coalesce(iou.withdraw_amount,0) - coalesce(rep.repayment_amount,0) as contract_balance, original_manage_id,apply_user,apply_date,depart, depart_id,currency_type_id,apply_user_id,calculation_status,change_date,assigned_number FROM fss_loan_contract_manage m left join (select manage_id,coalesce(sum(withdraw_amount),0) as withdraw_amount from fss_loan_contract_iou group by manage_id) as iou on iou.manage_id= m.manage_id left join (select manage_id,coalesce(sum(repayment_amount),0) as repayment_amount from fss_loan_contract_repayment group by manage_id) as rep on rep.manage_id=m.manage_id where m.deleted='1'
AND loan_type = #{loanContractManageVo.loanType}
AND project_name like concat('%', #{loanContractManageVo.projectName}, '%')
AND loan_organization like concat('%',#{loanContractManageVo.loanOrganization},'%')
AND loan_organization_id = #{loanContractManageVo.loanOrganizationId}
AND company_name = #{loanContractManageVo.companyName}
AND company_id = #{loanContractManageVo.companyId}
AND contract_name like concat('%', #{loanContractManageVo.contractName}, '%')
and company_id in (select bu.bukrs from fss_base_role_organ_busi bu join sec_user_role ur on bu.role_id=ur.role_id and ur.user_id=#{userId} group by bu.bukrs)
order by create_time desc nulls last
